import "core-js/modules/es.array.push.js";
import Plotly from 'plotly.js/dist/plotly';
export default {
  name: "LinePlot",
  props: ["selectedCompany", "selectedAlgorithm"],
  data: () => ({
    LinePlotData: {
      x: [],
      y: []
    }
  }),
  mounted() {
    this.fetchData();
  },
  methods: {
    async fetchData() {
      // req URL to retrieve single company from backend
      var reqUrl = 'http://127.0.0.1:5000/companies/' + this.$props.selectedCompany + '?algorithm=' + this.$props.selectedAlgorithm;
      console.log("ReqURL " + reqUrl);
      // await response and data
      const response = await fetch(reqUrl);
      const responseData = await response.json();
      // transform data to usable by lineplot
      responseData.profit.forEach(profit => {
        this.LinePlotData.x.push(profit.year);
        this.LinePlotData.y.push(profit.value);
      });
      // draw the lineplot after the data is transformed
      this.drawLinePlot();
    },
    drawLinePlot() {
      var trace1 = {
        x: this.LinePlotData.x.slice(0, -1),
        // excluding the last data point (predicted value for 2022)
        y: this.LinePlotData.y.slice(0, -1),
        // excluding the last data point (predicted value for 2022)
        type: 'scatter',
        line: {
          color: '#3B71CA',
          // Set default color for historical data
          width: 2
        }
      };
      var trace2 = {
        x: [this.LinePlotData.x[this.LinePlotData.x.length - 1]],
        // only the predicted year (2022)
        y: [this.LinePlotData.y[this.LinePlotData.y.length - 1]],
        // only the predicted value for 2022
        type: 'scatter',
        mode: 'markers',
        marker: {
          color: '#DC4C64',
          // Set color for the predicted value for 2022
          size: 10
        }
      };
      var data = [trace1, trace2];
      var layout = {
        // add axis titles
        xaxis: {
          title: 'Years',
          showgrid: false,
          zeroline: false
        },
        yaxis: {
          title: 'Profit',
          showline: false
        }
      };
      var config = {
        responsive: true,
        displayModeBar: false
      };
      Plotly.newPlot('myLinePlot', data, layout, config);
    }
  },
  watch: {
    selectedCompany() {
      this.LinePlotData.x = [];
      this.LinePlotData.y = [];
      this.fetchData();
    },
    selectedAlgorithm() {
      this.LinePlotData.x = [];
      this.LinePlotData.y = [];
      this.fetchData();
      this.drawLinePlot(); // Update the plot when the algorithm changes
    }
  }
};
