import "core-js/modules/es.array.push.js";
import Plotly from 'plotly.js/dist/plotly';
export default {
  name: "BarChart",
  props: ["selectedCategory", "selectedCompany", "averageEmployeeCount"],
  data: () => ({
    BarChartData: {
      x: [],
      y: [],
      name: []
    }
  }),
  mounted() {
    this.fetchData();
  },
  methods: {
    async fetchData() {
      // req URL to retrieve companies from backend
      var reqUrl = 'http://127.0.0.1:5000/companies?category=' + this.$props.selectedCategory;
      console.log('ReqURL ' + reqUrl);
      // await response and data
      const response = await fetch(reqUrl);
      const responseData = await response.json();
      responseData.forEach(company => {
        this.BarChartData.name.push(company.name);
        this.BarChartData.x.push(company.name);
        this.BarChartData.y.push(company.employees);
      });
      // after the data is loaded, draw the plot
      this.drawBarChart();
    },
    drawBarChart() {
      // Calculate the average employee count for the category
      const averageEmployeeCount = this.calculateAverageEmployeeCount(this.BarChartData.y);
      var trace1 = {
        x: this.BarChartData.x,
        y: this.BarChartData.y,
        mode: 'markers',
        type: 'bar',
        // Change the type to bar
        // text: this.BarChartData.name,
        // marker: {
        //   color: this.BarChartData.color,  // Use the color data for markers
        //   size: 12
        // },
        marker: {
          color: '#3777ee',
          // set color for bars
          opacity: 0.7
        },
        legendgroup: 'Companies',
        name: 'Companies'
      };
      var trace2 = {
        x: this.BarChartData.x,
        y: Array(this.BarChartData.x.length).fill(averageEmployeeCount),
        // Array of average values for the average line
        type: 'scatter',
        // Change to 'scatter' type
        mode: 'lines',
        // Set mode to 'lines' to remove markers
        line: {
          color: '#e74c3c',
          // Red color for the average line
          width: 2
        },
        legendgroup: 'Average',
        name: 'Average Employees',
        hoverinfo: 'y',
        // Show only the y value (average number of employees) on hover
        text: Array(this.BarChartData.x.length).fill('Average') // Add the label "Average"
      };
      // Call a method after creating the plot which is responsible to handle the click events.
      var data = [trace1, trace2];
      var layout = {
        // Add axis titles
        xaxis: {
          title: 'Companies',
          showgrid: false,
          zeroline: false
        },
        yaxis: {
          title: 'Number of Employees',
          showline: false
        },
        showlegend: false,
        // Hide the legend
        // Add legend settings to the layout object
        // legend: {
        //   tracegroupgap: 10,  // Gap between legend entries
        //   itemsizing: 'constant'  // Legend item size is constan
        // },
        margin: {
          b: 180 // Adjust this value to move the x-axis label lower
        }
      };

      var config = {
        responsive: true,
        displayModeBar: false
      };
      Plotly.newPlot('myBarChart', data, layout, config);
      this.clickBarChart();
    },
    // Calculate the average employee count for the category
    calculateAverageEmployeeCount(employeeCounts) {
      const total = employeeCounts.reduce((acc, count) => acc + count, 0);
      return total / employeeCounts.length;
    },
    // Upon a click, retrieve the point number and then emit an event with (pn+1 = company id).
    // Emitting an event will notify the parent (configuration panel) that the selected company has changed.
    // Then, revert all colors to black in case of previous clicks and change the current selection to blue.
    // Then, call the Plotly. restyle function to update the plot.
    clickBarChart() {
      // var pn = 0
      var that = this;
      var myPlot = document.getElementById('myBarChart');
      myPlot.on('plotly_click', function (data) {
        for (var i = 0; i < data.points.length; i++) {
          // get the index of point
          // pn = data.points[i].pointNumber;

          // Get the index of point and retrieve the associated company name
          var pn = data.points[i].pointNumber;
          var companyName = that.BarChartData.name[pn];

          // emit event to change the currently selected company in the
          // a) configuration panel
          // and b) update the Profit View
          that.$emit('changeCurrentlySelectedCompany', companyName);

          // revert all colors
          var colors = ['#00000' * 15];

          // and change currently selected color to blue
          colors[pn] = '#3777ee';

          // update the marker and plot
          var update = {
            'marker': {
              color: colors,
              size: 12
            }
          };
          Plotly.restyle('myBarChart', update);
        }
      });
    }
  },
  watch: {
    selectedCategory: function () {
      this.BarChartData.x = [];
      this.BarChartData.y = [];
      this.fetchData();
    }
  }
};
